import com.wuweibi.module4j.ModuleActivatorimport com.wuweibi.module4j.SupperModuleimport com.wuweibi.module4j.module.Moduleimport com.wuweibi.module4j.module.ModuleContextimport org.marker.mushroom.ext.menu.MenuUtilsimport org.marker.mushroom.beans.Menuimport org.marker.mushroom.ext.model.ContentModelContextimport org.marker.mushroom.ext.plugin.Pluginletimport org.marker.mushroom.ext.plugin.PluginContext;/** * 模块 *  * @author marker *  */class PluginsActivatorImpl extends SupperModule {		/**	 * 启动模块	 * @param context	 * @param module	 * @throws Exception	 */	void start(ModuleContext context) throws Exception {				Class<?> clzz = require("ManagerPluginsPluginlet");		// 模型		Class<?> modelClass = require("ManagerPluginsModel");		if(null != clzz){			// 插件管理			PluginContext pluginContext = PluginContext.getInstance();			pluginContext.put(clzz.newInstance());			// 加载插件管理模型			ContentModelContext contentModelContext = ContentModelContext.getInstance();			contentModelContext.put(modelClass.newInstance())			//			Menu menu = new Menu();//			menu.setIcon("fa-comments-o");//			menu.setName("留言管理");//			menu.setUrl("/plugin/guestbook/list");//			menu.setModuleId(module.getId());// 模块ID//			menu.setSort(1000)////			MenuUtils menuUtils = MenuUtils.getInstance();////			menuUtils.builder("bb2bc5fb802544a2a4634013d2c19936", menu );		}else{            logger.error("插件加载失败", module.getConfig());		}        // 加载插件标签					} 	void stop(ModuleContext context) throws Exception { 		MenuUtils menuUtils = MenuUtils.getInstance();		menuUtils.remove(this.id);		 	}}